31.query:
// Real example of doing a lookup to compare against the number of orders packed last week, for the same hour.
fetch bizevents, from: -1h
| filter event.provider == "acme.retail" and event.type == "order.packed"
| summarize ordersPackedNow = count(), by:{event.type}
| lookup [fetch bizevents, from: -1d-1h, to:-1d | filter event.provider == "acme.retail" and event.type == "order.packed" | summarize ordersPackedLastWeek = count(), by:{event.type} ], sourceField:event.type, lookupField:event.type
| fieldsAdd percentageChange = ((toDouble(lookup.ordersPackedLastWeek) - toDouble(ordersPackedNow)) / toDouble(ordersPackedNow)) * 100

