78.query:
// Real example from the data looking at the time between shipping and order being received, for "Gold" members.
fetch bizevents
| filter event.provider == "acme.retail" 
| filter event.type == "order.shipped" or event.type == "order.received" or event.type == "place.order"
| sort timestamp asc
| fieldsAdd placeOrderTimestamp = if(event.type == "place.order", timestamp), receivedTimestamp = if(event.type == "order.received", timestamp)
| summarize placeOrderTime = takeMin(placeOrderTimestamp), receivedTime = takeMin(receivedTimestamp), loyalty = takeAny(loyalty), by:{order.id}
| filterOut isNull(receivedTime) or isNull(placeOrderTime)
| fieldsAdd totalTime = receivedTime - placeOrderTime
| summarize `End-to-end order time` = avg(totalTime), by:{loyalty}
